import React, { Component } from 'react'
import {  Container,  Image,  Menu, Visibility,} from "semantic-ui-react";
import { menuStyle,fixedMenuStyle } from '../helpers/styleHelpers';
import { Link,NavLink } from 'react-router-dom';

export default class Header extends Component {

    state = {
        menuFixed: false,
        overlayFixed: false,
      }


      stickOverlay = () => this.setState({ overlayFixed: true })

      stickTopMenu = () => this.setState({ menuFixed: true })

      unStickOverlay = () => this.setState({ overlayFixed: false })

      unStickTopMenu = () => this.setState({ menuFixed: false })


    render() {
        const { menuFixed} = this.state
        return (
            <div>

        <Visibility
          onBottomPassed={this.stickTopMenu}
          onBottomVisible={this.unStickTopMenu}
          once={false}
        >
          <Menu
            borderless
            fixed={menuFixed ? 'top' : undefined}
            style={menuFixed ? fixedMenuStyle : menuStyle}
          >
            <Container text>
              <Menu.Item>
                <Image size='mini' src='https://react.semantic-ui.com/logo.png' />
              </Menu.Item>
              <Menu.Item header as={NavLink} to="/" exact>Movie App</Menu.Item>
              <Menu.Item exact as={NavLink} to="/movies">Movies</Menu.Item>
              <Menu.Item  exact as={NavLink} to="/movies/new">Add New</Menu.Item>            
            </Container>
          </Menu>
        </Visibility>

            </div>
        )
    }
}
